package br.com.m2m.meuonibus.models.db;import android.content.Context;import android.database.sqlite.SQLiteDatabase;import android.database.sqlite.SQLiteOpenHelper;import android.util.Log;public class AppDataBase extends SQLiteOpenHelper {		  private static final String DATABASE_NAME = "meuonibus1.db";	  private static final int DATABASE_VERSION = 1;	public AppDataBase(Context context) {		super(context, DATABASE_NAME, null, DATABASE_VERSION);	}	@Override	public void onCreate(SQLiteDatabase db) {		PontoTable.onCreate(db);		LocalizacaoTable.onCreate(db);	}	@Override	public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {		Log.d(PontoTable.class.getName(), "Upgrading database from version "				+ oldVersion + " to " + newVersion				+ ", which will destroy all old data");		db.execSQL("DROP TABLE IF EXISTS " + PontoTable.TABLE_PONTO);		db.execSQL("DROP TABLE IF EXISTS " + LocalizacaoTable.TABLE_LOCALIZACAO);		PontoTable.onCreate(db);		LocalizacaoTable.onCreate(db);	}}