package br.com.m2m.meuonibus.astur.activities;import android.os.Bundle;import android.support.v4.app.Fragment;import br.com.m2m.meuonibus.astur.R;import br.com.m2m.meuonibus.astur.fragments.OnibusDetalheFragment;import br.com.m2m.meuonibuscommons.activities.base.BaseWithImageAndTitleActivity;import br.com.m2m.meuonibuscommons.models.LinhaOnibus;import br.com.m2m.meuonibuscommons.models.Trajeto;public class OnibusDetalheActivity extends BaseWithImageAndTitleActivity {		public static final String EXTRA_ONIBUS_DETALHE_LINHA = OnibusDetalheActivity.class.getName()			+ "#linha";	public static final String BUNDLE_ONIBUS_DETALHE_LINHA = OnibusDetalheActivity.class.getName()			+ "#bundle_linha";	public static final String EXTRA_ONIBUS_DETALHE_TRAJETO = OnibusDetalheActivity.class.getName()			+ "#trajeto";		public LinhaOnibus mLinha;	public Trajeto mTrajeto;		@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);				setContentView(R.layout.activity_onibus_detalhe);		try {			mLinha = (LinhaOnibus) getIntent().getSerializableExtra(EXTRA_ONIBUS_DETALHE_LINHA);			mTrajeto = (Trajeto) getIntent().getSerializableExtra(EXTRA_ONIBUS_DETALHE_TRAJETO);		} catch (Exception e) {			e.printStackTrace();		}		if (mLinha != null) {			setActionBarImageAndTitle(mLinha.name);		} else {			setActionBarImageAndTitle("Linha:");		}			if (savedInstanceState == null) {			Fragment fragment = OnibusDetalheFragment.newInstance(mLinha, mTrajeto);			getSupportFragmentManager().beginTransaction()					.add(R.id.container_onibus_detalhe, fragment).commit();		}	}}