package br.com.m2m.meuonibuscommons.activities.base;import android.annotation.SuppressLint;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.widget.TextView;import br.com.m2m.meuonibuscommons.R;public class BaseWithTitleActivity extends BaseActivity {		TextView tilleTextView;		@SuppressLint("InflateParams") @Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);				LayoutInflater inflator = LayoutInflater.from(this);		View v = inflator.inflate(R.layout.actionbar_custom_with_title, null);		tilleTextView =((TextView)v.findViewById(R.id.title_custom_actionBar));	        actionBar.setCustomView(v);        actionBar.setDisplayShowTitleEnabled(false);        actionBar.setDisplayShowCustomEnabled(true);  	}		public void setActionBarTitle(String title) {		tilleTextView.setText(title);	}}