package br.com.m2m.meuonibuscommons.handlers;import com.loopj.android.http.JsonHttpResponseHandler;public class CustomJsonHttpResponseHandler extends JsonHttpResponseHandler {	@Override	public void onFailure(int statusCode, org.apache.http.Header[] headers,			java.lang.String responseString, java.lang.Throwable throwable) {		setErro(throwable);	}	@Override	public void onFailure(int statusCode, org.apache.http.Header[] headers,			java.lang.Throwable throwable, org.json.JSONObject errorResponse) {		setErro(throwable);	}	@Override	public void onFailure(int statusCode, org.apache.http.Header[] headers,			java.lang.Throwable throwable, org.json.JSONArray errorResponse) {		setErro(throwable);	}	public void setErro(Throwable e) {	}}