package br.com.m2m.meuonibuscommons.models.db;import android.database.sqlite.SQLiteDatabase;import android.util.Log;public class LocalizacaoTable {	public static final String TABLE_LOCALIZACAO = "locais";	public static final String COLUMN_ID = "_id";	public static final String COLUMN_NOME = "nome";	public static final String COLUMN_ENDERECO = "endereco";	public static final String COLUMN_LATITUDE = "lat";	public static final String COLUMN_LONGITUDE = "lng";	public static final String COLUMN_DATA = "data";	public static String[] ALLCOLUMNS = { COLUMN_ID,			COLUMN_NOME, COLUMN_ENDERECO, COLUMN_LATITUDE, COLUMN_LONGITUDE };	private static final String TEXT_TYPE = " text";	private static final String TEXT_TYPE_NOT_NULL = " text not null";	private static final String COMMA_SEP = ",";	private static final String DATABASE_CREATE = "create table " + TABLE_LOCALIZACAO			+ "(" + COLUMN_ID + " integer primary key autoincrement, "			+ COLUMN_NOME + TEXT_TYPE_NOT_NULL			+ COMMA_SEP + COLUMN_ENDERECO + TEXT_TYPE + COMMA_SEP			+ COLUMN_LATITUDE + " real" + COMMA_SEP			+ COLUMN_LONGITUDE + " real" + COMMA_SEP			+ COLUMN_DATA + " datetime" + ");";	public static void onCreate(SQLiteDatabase database) {		Log.d("Criando tabela: ",TABLE_LOCALIZACAO);		database.execSQL(DATABASE_CREATE);	}}