package br.com.how.hhwslidemenu;import android.graphics.drawable.Drawable;import android.support.v4.app.Fragment;public class HHWMenuItem {	public String itemName;	public Drawable icon;	public Object activityClass;	public Fragment fragmentClass;	public boolean isFragment;	public HHWMenuItem(String name, Object activityClass, Drawable icon) {		this.itemName = name;		this.icon = icon;		this.activityClass = activityClass;		this.fragmentClass = null;		this.isFragment = false;	}	public HHWMenuItem(String name, Fragment fragment, Drawable icon) {		this.itemName = name;		this.icon = icon;		this.activityClass = null;		this.fragmentClass = fragment;		this.isFragment = true;	}	public HHWMenuItem(String name, Object activityClass) {		this.itemName = name;		this.icon = null;		this.activityClass = activityClass;		this.fragmentClass = null;		this.isFragment = false;	}	public HHWMenuItem(String name, Fragment fragment) {		this.itemName = name;		this.icon = null;		this.activityClass = null;		this.fragmentClass = fragment;		this.isFragment = true;	}}