package br.com.hhw.startapp.activities;import android.os.Bundle;import br.com.hhw.startapp.R;import br.com.hhw.startapp.handlers.CustomDialogHandler;import br.com.hhw.startapp.helpers.CustomDialogHelper;import br.com.how.hhwslidemenu.HHWMenuActivity;public abstract class MenuStartAppActivity extends HHWMenuActivity {		protected abstract void onCreateStartApp();	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		onCreateStartApp();	}	@Override	public void onBackPressed() {		slideMenu.mDrawerLayout.closeDrawers();		if (slideMenu.getStartFragment().isVisible()) {			showDialogToFinish();		} else {			super.onBackPressed();		}	}	private void showDialogToFinish() {		String text = getString(R.string.voce_realmente_deseja_sair_do_aplicativo_);		CustomDialogHelper.showGenericDialog(this, text,				new CustomDialogHandler() {					@Override					public void setOk() {						finish();					}				});	}}